      g.DrawImage(
         _Bitmap, 
         new Rectangle(0, 0, pictureBox1.Width, pictureBox1.Height), 
         new Rectangle(p.X, p.Y, pictureBox1.Width, pictureBox1.Height), 
         GraphicsUnit.Pixel);
   }

   // Dispose and assign temp image 
   pictureBox1.Image?.Dispose();
   pictureBox1.Image = target;
