    {
        base.OnMouseLeave(e);
        _mouseUnder = false;
        SetImage();
    }

    private void SetImage()
    {
        try
        {
            if (_state)
                BackImage = _activateState;
            else
                BackImage = _mouseUnder ? _mouseUpState : _normalState;
        }
        catch (Exception)
